import React from 'react';
import './App.css';
import { Button } from 'react-bootstrap';

function App() {

  const [viewStyle, setMyStyle] = React.useState("board-view");

  const changeStyle = () => {
    if(myStyle === "list-view") {
      setMyStyle("board-view");
    } else {
      setMyStyle("list-view");
    }
  }

  return (
    <div>
      <h1 className={myStyle}>Hello world!</h1>
      <Button style={{width: '40px', height: '40px'}} onClick={changeStyle}/>
    </div>
  );
}

export default App;
