import React from 'react';
import './App.css';
import { Button } from 'react-bootstrap';

function App() {

  // const view_style = {
  //   list: "list-view",
  //   board: "board-view"
  // }

  let view_style = "";
  let boolval = false;
  if(boolval === false) {
    view_style = "list-view";
  } else if (boolval === true) {
    view_style = "board-view";
  }

  return (
    <div>
      <h1 className={view_style}>Hello world!</h1>
      <Button style={{marginLeft: '40px',width: '40px', height: '40px'}} onClick={() => boolval = !boolval}/>
    </div>
  );
}

export default App;
