import React from 'react';
import './App.css';
import { Button } from 'react-bootstrap';

function App() {
  state = {
    myStyle: changeStyle()
  }

  // const view_style = {
  //   list: "list-view",
  //   board: "board-view"
  // }
  let boolval = false;
  let view_style = changeStyle();

  function changeStyle() {
    boolval = !boolval;
    if(boolval === false) {
      return "list-view";
    } else if (boolval === true) {
      return "board-view";
    }
  }

  return (
    <div>
      <h1 className={view_style}>Hello world!</h1>
      <Button style={{marginLeft: '40px',width: '40px', height: '40px'}} onClick={changeStyle}/>
    </div>
  );
}

export default App;
