import React from 'react';
import './index.css';
import { Button, Navbar, Nav, NavDropdown, Form, FormControl, Brand, Toggle, NavItem } from 'react-bootstrap';
import { Switch, Route, BrowserRouter as Router } from "react-router-dom";
import star from '../images/star.png'



function Video(props) {

    const iframeVal = `https://www.youtube.com/embed/${props.videoDetails.id_on_youtube}`

    const videoContainer= {
        border: '1px solid black',
        textAlign: 'center',
        margin: '20px 10px',
        width: '30%',
        height: '290px'
    }

    return (
        <div style={videoContainer}>
            <iframe width="100%" height="auto" src={iframeVal} frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
            <p>{props.videoDetails.title}</p>
            <p>{props.videoDetails.date}</p>
            <p>{starMaker(props.videoDetails.rating)}</p>
            <Button >Delete</Button> <Button ></Button>
        </div>

    );
}

export default Video;

function starMaker(rating) {
    for( let i=0 ; i < rating ; i++) {
        return (<img id="rating-star" alt="star-icon" src={star} />)
    }
}
