import React, { useState, useEffect } from 'react';
import './index.css';
import db from './firebaseConfig';
import LeftSidebar from './LeftSidebar'
import Video from './Video'




function AllVideos(props) {

  const [videosList, setvideosList] = React.useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const res = await db.collection('YouTube').get();
      const documentIds = res.docs.map(video=> video.id);
      const data = res.docs.map((video, index)=> Object.assign(video.data(), {id_on_firebase: documentIds[index]}));
      setvideosList(data);
    };
    fetchData();
  }, []);

  return (
    <div id="videos-container">
        <LeftSidebar />
        <div id="right-side">
            <div id="videosContainer">

            {
              videosList.map( video => {
                if(!video.isArchived) {
                   return <Video videoDetails={video} />
                }
              })
            }
            </div>
        </div>
    </div>
  );
}

export default AllVideos;
