import React from 'react';
import './App.css';
import { Button } from 'react-bootstrap';

function App() {

  let boolval = false;
  let currentStyle = "list-view";
  function changeStyle() {
    boolval = !boolval;
    if(boolval === false) {
      currentStyle = "list-view";
    } else if (boolval === true) {
      currentStyle = "board-view";
    }
  }

  const [myStyle, setMyStyle] = React.useState("list-view");

  return (
    <div>
      <h1 className={this.state.myStyle}>Hello world!</h1>
      <Button style={{marginLeft: '40px',width: '40px', height: '40px'}} onClick={changeStyle}/>
    </div>
  );
}

export default App;
