import React from 'react';
import './index.css';
import { Button, Navbar, Nav, NavDropdown, Form, FormControl, Brand, Toggle, NavItem } from 'react-bootstrap';
import { Switch, Route, BrowserRouter as Router } from "react-router-dom";
import star from '../images/star.png'
import db from '../firebaseConfig';



function Video(props) {

    const id_on_youtube = props.videoDetails.id_on_youtube;

    function deleteVideo(id_on_youtube) {

        let desertRef = storageRef.child(`YouTube/${id}`);


        const docsWillBeDeleted = db.collection('YouTube').where('id_on_youtube','==',id_on_youtube);
        docsWillBeDeleted.get().then(function(querySnapshot) {
            querySnapshot.forEach(function(doc) {
                doc.ref.delete();
            });
        });
    }

    const iframeVal = `https://www.youtube.com/embed/${props.videoDetails.id_on_youtube}`

    const videoContainer= {
        border: '1px solid black',
        textAlign: 'center',
        margin: '20px 10px',
        width: '25%',
        height: 'auto',
        paddingBottom: '8px'
        // position: 'relative',
    }

    const elementStyle = {
        marginBottom: '-4px'
    }

    return (
        <div style={videoContainer}>
            <iframe width="100%" height="auto" src={iframeVal} frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
            <p style={elementStyle}>{props.videoDetails.title}</p>
            <p style={elementStyle}>{props.videoDetails.date}</p>
            <p style={elementStyle}>{starMaker(props.videoDetails.rating)}</p>
            <Button onClick={deleteVideo} style={elementStyle} variant="danger">Delete</Button>
            <Button style={elementStyle} variant="secondary">Archive</Button>
        </div>

    );
}

export default Video;

function starMaker(rating) {
    for( let i=0 ; i < rating ; i++) {
        return (<img id="rating-star" alt="star-icon" src={star} />)
    }
}
