import React from 'react';
import './index.css';
import { Button, Navbar, Nav, NavDropdown, Form, FormControl, Brand, Toggle, NavItem } from 'react-bootstrap';
import { Switch, Route, BrowserRouter as Router } from "react-router-dom";
import star from '../images/star.png'
import db from '../firebaseConfig';



function Video(props) {

    const id_on_firebase = props.videoDetails.id_on_firebase;

    function deleteVideo() {
        db.collection('YouTube').doc(id_on_firebase).delete();
    }

    function archive() {
        db.collection("YouTube").doc(id_on_firebase).update({
            isArchived: true
          });
    }

    function isViewed() {
        db.collection("YouTube").doc(id_on_firebase).update({
            isViewed: true
          });
    }


    const iframeSRC = `https://www.youtube.com/embed/${props.videoDetails.id_on_youtube}`

    const videoContainer= {
        border: '1px solid black',
        textAlign: 'center',
        margin: '20px 10px',
        width: '25%',
        height: 'auto',
        paddingBottom: '8px'
        // position: 'relative',
    }

    const elementStyle = {
        marginBottom: '-4px'
    }

    return (
        <div style={videoContainer}>
            {/* Setting video frame */}
            <iframe width="100%" height="auto" src={iframeSRC} frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>

            {/* Displaying video title */}
            <p style={elementStyle}>{props.videoDetails.title}</p>

            {/* Displaying date of video adding */}
            <p style={elementStyle}>{props.videoDetails.date}</p>

            {/* Displaying video rating */}
            <p style={elementStyle}>{starMaker(props.videoDetails.rating)}</p>

            {/* Creating a button to delete the video */}
            {
              props.videos.map( video => {
                if(!props.videoDetails.isViewed) {
                   return <Video videoDetails={video} />
                }
              })
            }


            {/* Creating a button to set the video as archived */}
            <Button onClick={archive} style={elementStyle} variant="secondary">Archive</Button>

            {/* Creating a button to set the video as viewed */}
            <Button onClick={isViewed} style={elementStyle} variant="success">Set as Viewd</Button>
        </div>

    );
}

export default Video;

function starMaker(rating) {
    for( let i=0 ; i < rating ; i++) {
        return (<img id="rating-star" alt="star-icon" src={star} />)
    }
}
