import React, { useState, useEffect } from 'react';
import './App.css';
import db from './firebaseConfig';
import { Switch, Route, Link, BrowserRouter as Router } from "react-router-dom";
import Header from './components/Header'
import AllVideos from './components/AllVideos'
import UnViewed from './components/UnViewed'
import Viewed from './components/Viewed'
import Archive from './components/Archive'
import Footer from './components/Footer'
import Video from './components/Footer'





function App() {

  const [videos, setVideos] = React.useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const res = await db.collection('YouTube').get();
      // const documentIds = res.docChanges().map(id=>id.id);
      const documentIds = res.docs.map(video=> video.id);
      // setVideos(documentIds)
      const data = res.docs.map((video, index)=> Object.assign(video.data(), {id_on_firebase: documentIds[index]}));
      setVideos(data);
    };
    fetchData();
  }, []);

  const [viewStyle, setViewStyle] = React.useState("board-view");

  const changeStyle = () => {
    if(viewStyle === "list-view") {
      setViewStyle("board-view");
    } else {
      setViewStyle("list-view");
    }
  }


  return (
    <Router id="page-body">

      <Header />

      <Switch>
        <Route exact path="/">
          <AllVideos videos={videos}/>
        </Route>
        <Route path="/allVideos"> 
          <AllVideos videos={videos}/>
        </Route>
        <Route path="/unviewed"> 
          <UnViewed videos={videos}/>
        </Route>
        <Route path="/viewed"> 
          <Viewed videos={videos}/>
        </Route>
        <Route path="/archive"> 
          <Archive videos={videos}/>
        </Route>
      </Switch>

      {
              videos.map( video => {
                if(!video.isArchived) {
                   return <Video videoDetails={video} />
                }
              })
            }

      <Footer />

    </Router>

  );
}

export default App;
